REPLACE INTO `npc_text` SET `ID`=15117,`prob0`=1,`text0_0`='My commander was butchered before my eyes - my brothers dead all around me - I could not fight on. I am so ashamed, $c. I did not avenge them.',`text0_1`='',`lang0`=0,`em0_0`=1,`em0_1`=0,`em0_2`=0,`em0_3`=0,`em0_4`=0,`em0_5`=0,`prob1`=1,`text1_0`='We grunts were ordered to charge once more - into a wall of pikes - I could not do it, $c. My weapon slid from my hands and my legs did not move.$B$BI am dishonored.',`text1_1`='',`lang1`=0,`em1_0`=1,`em1_1`=0,`em1_2`=0,`em1_3`=0,`em1_4`=0,`em1_5`=0,`prob2`=1,`text2_0`='Do not look at me, $r. I have disgraced the Horde. I am done.',`text2_1`='',`lang2`=0,`em2_0`=1,`em2_1`=0,`em2_2`=0,`em2_3`=0,`em2_4`=0,`em2_5`=0,`prob3`=1,`text3_0`='No orc should ever flee from battle as I did. Leave me to my disgrace. Would that I would die in this tower, but I dread learning what my ancestors think of my cowardice.',`text3_1`='',`lang3`=0,`em3_0`=1,`em3_1`=0,`em3_2`=0,`em3_3`=0,`em3_4`=0,`em3_5`=0,`prob4`=0,`text4_0`='',`text4_1`='',`lang4`=0,`em4_0`=0,`em4_1`=0,`em4_2`=0,`em4_3`=0,`em4_4`=0,`em4_5`=0,`prob5`=0,`text5_0`='',`text5_1`='',`lang5`=0,`em5_0`=0,`em5_1`=0,`em5_2`=0,`em5_3`=0,`em5_4`=0,`em5_5`=0,`prob6`=0,`text6_0`='',`text6_1`='',`lang6`=0,`em6_0`=0,`em6_1`=0,`em6_2`=0,`em6_3`=0,`em6_4`=0,`em6_5`=0,`prob7`=0,`text7_0`='',`text7_1`='',`lang7`=0,`em7_0`=0,`em7_1`=0,`em7_2`=0,`em7_3`=0,`em7_4`=0,`em7_5`=0;
