UPDATE `quest_template` SET `OfferRewardText`='Nice one, $N!  I tell ya, golden stonefish are not easy to come by so this is a nice treat.' WHERE `entry`=26572; -- -Unknown-
UPDATE `quest_template` SET `OfferRewardText`='<Gar''dul breaks into a sweat.>$B$BLook - ah - let''s not discuss this here, in front of the men...' WHERE `entry`=24577; -- -Unknown-
