UPDATE `quest_template` SET `OfferRewardText`='<Calder peers into the sack of extremities and licks his lips with a dry, black tongue.>$B$BOh my. This is exciting.$B$B Aren''t you excited, $N?' WHERE `entry`=24619; -- -Unknown-
